import app from 'firebase/app';
import 'firebase/auth';
import 'firebase/firebase-firestore';

var firebaseConfig = {
  apiKey: "AIzaSyA7XPj2zFd26B9bh_zDQ2aDf38uACH3dYI",
  authDomain: "reachzone-a5637.firebaseapp.com",
  databaseURL: "https://reachzone-a5637.firebaseio.com",
  projectId: "reachzone-a5637",
  storageBucket: "reachzone-a5637.appspot.com",
  messagingSenderId: "282619171712",
};

class Firebase {
	constructor() {
		app.initializeApp(firebaseConfig) //initialize configurations
		this.auth = app.auth()
		this.db = app.firestore()
	}

	async login(email, password) {
		await this.auth.signInWithEmailAndPassword(email, password)
		.then((user) => localStorage.setItem('name', user.user.displayName))
		.catch((err) => console.log(err));
	}

	async logout() {
		await this.auth.signOut()
		.then(() => localStorage.removeItem('name')) //on success remove name from local storage
		.catch((err) => console.log(err));
	}

	async register(name, email, password) {

		await this.auth.createUserWithEmailAndPassword(email, password)
		.then(async (user) => {
			try {

				await this.auth.currentUser.updateProfile({
					displayName: name
				}); //changes the display name

			} catch(err) {
				console.log(err)
			}

			localStorage.setItem('name', user.user.displayName);
		})

	}

	createTodo() {
		console.log(this.auth.uid);
	}
}

export default new Firebase()
